package com.git.bean;

public class Even {

	//instance variables
	private Integer num1,num2;

	//default constructor
	public Even() {
		super();
		// TODO Auto-generated constructor stub
	}

	//parameterised constructor
	public Even(Integer num1, Integer num2) {
		super();
		this.num1 = num1;
		this.num2 = num2;
	}

	//toString method
	public String toString() {
		return "Even [num1=" + num1 + ", num2=" + num2 + "]";
	}
	
	
	
}
